import { FormControl, Validators, FormBuilder } from "@angular/forms"; //create the validation object model
//connect  the validationg to the UI
//check if it is valid or not

export class Customer {
  constructor() {
    this.customerCode = "";
    this.customerName = "";
    this.customerAmount = 0;

    var _builder = new FormBuilder();

    this.formCustomerGroup = _builder.group({});
    this.formCustomerGroup.addControl('CustomerNameControl', new FormControl('', Validators.required));
    var validationcollections = [];
    validationcollections.push(Validators.required);
    validationcollections.push(Validators.pattern("^[0-9]{4,4}$"));
    this.formCustomerGroup.addControl('CustomerCodeControl', new FormControl('', Validators.compose(validationcollections)));
  }

}
